import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { Selection } from 'd3';
import { GanttChartBlock } from '../utils/types';

type D3Selection = Selection<any, any, any, any>;

interface GanttChartProps {
    data: GanttChartBlock[];
    width?: number;
    height?: number;
}

const GanttChart: React.FC<GanttChartProps> = ({ data, width = 800, height = 200 }) => {
    const svgRef = useRef<SVGSVGElement>(null);

    useEffect(() => {
        if (!data.length || !svgRef.current) return;

        // Clear previous chart
        d3.select(svgRef.current).selectAll('*').remove();

        const margin = { top: 20, right: 30, bottom: 40, left: 60 };
        const chartWidth = width - margin.left - margin.right;
        const chartHeight = height - margin.top - margin.bottom;

        const svg = d3.select(svgRef.current)
            .attr('width', width)
            .attr('height', height);

        const chart = svg.append('g')
            .attr('transform', `translate(${margin.left},${margin.top})`);

        // Create scales
        const timeScale = d3.scaleLinear()
            .domain([0, d3.max(data, d => d.endTime) || 0])
            .range([0, chartWidth]);

        const uniqueProcessIds = Array.from(new Set(data.map(d => d.processId)));
        const processScale = d3.scaleBand()
            .domain(uniqueProcessIds)
            .range([0, chartHeight])
            .padding(0.1);

        const processColors: { [key: string]: string } = {};
        uniqueProcessIds.forEach((id, index) => {
            processColors[id] = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
        });

        // Add axes
        const xAxis = d3.axisBottom(timeScale);
        const yAxis = d3.axisLeft(processScale);

        chart.append('g')
            .attr('class', 'x-axis')
            .attr('transform', `translate(0,${chartHeight})`)
            .call(xAxis)
            .append('text')
            .attr('class', 'axis-label')
            .attr('x', chartWidth / 2)
            .attr('y', 35)
            .attr('fill', 'black')
            .style('text-anchor', 'middle')
            .text('Time');

        chart.append('g')
            .attr('class', 'y-axis')
            .call(yAxis)
            .append('text')
            .attr('class', 'axis-label')
            .attr('transform', 'rotate(-90)')
            .attr('x', -chartHeight / 2)
            .attr('y', -45)
            .attr('fill', 'black')
            .style('text-anchor', 'middle')
            .text('Process ID');

        // Add Gantt blocks
        chart.selectAll('rect')
            .data(data)
            .enter()
            .append('rect')
            .attr('x', (d: GanttChartBlock) => timeScale(d.startTime))
            .attr('y', (d: GanttChartBlock) => processScale(d.processId) || 0)
            .attr('width', (d: GanttChartBlock) => timeScale(d.endTime) - timeScale(d.startTime))
            .attr('height', processScale.bandwidth())
            .attr('fill', (d: GanttChartBlock) => d.color)
            .attr('rx', 3)
            .attr('ry', 3);

        // Add tooltips
        chart.selectAll('rect')
            .append('title')
            .text(function(this: HTMLTitleElement, d: unknown) {
                const block = d as GanttChartBlock;
                return `Process: ${block.processId}\nStart: ${block.startTime}\nEnd: ${block.endTime}\nDuration: ${block.endTime - block.startTime}`;
            });

        // Add time labels
        chart.selectAll('.time-label')
            .data(data)
            .enter()
            .append('text')
            .attr('class', 'time-label')
            .attr('x', (d: GanttChartBlock) => timeScale(d.startTime) + (timeScale(d.endTime) - timeScale(d.startTime)) / 2)
            .attr('y', (d: GanttChartBlock) => (processScale(d.processId) || 0) + processScale.bandwidth() / 2)
            .attr('dy', '0.35em')
            .attr('text-anchor', 'middle')
            .style('fill', 'white')
            .style('font-size', '12px')
            .text((d: GanttChartBlock) => d.endTime - d.startTime);

    }, [data, width, height]);

    return (
        <div className="overflow-x-auto">
            <svg ref={svgRef} className="gantt-chart"></svg>
        </div>
    );
};

export default GanttChart;
